app = angular.module('app', ['ngMaterial', 'ui.bootstrap', 'ui.select2','mdPickers']);
app.filter('startFrom', function () {
    return function (input, start) {
        if (input) {
            start = +start;
            return input.slice(start);
        }
        return [];
    }
});

app.controller('ActionsForm', function () {
    this.topDirections = ['left', 'up'];
    this.bottomDirections = ['down', 'right'];

    this.isOpen = false;

    this.availableModes = ['md-fling', 'md-scale'];
    this.selectedMode = 'md-fling';

    this.availableDirections = ['up', 'down', 'left', 'right'];
    this.selectedDirection = 'right';
});

app.controller('AppCtrl', function ($scope, $mdDialog) {
    $scope.showAlert = function (ev) {
        $mdDialog.show(
                $mdDialog.alert()
                .parent(angular.element(document.querySelector('#popupContainer')))
                .clickOutsideToClose(true)
                .title('This is an alert title')
                .textContent('You can specify some description text in here.')
                .ariaLabel('Alert Dialog Demo')
                .ok('Got it!')
                .targetEvent(ev)
                );
    };
    
    
});

app.controller('Notification', Notification);
function Notification($scope, $mdSidenav) {
    $scope.openLeftMenu = function () {
        $mdSidenav('left').toggle();
    };
    $scope.openRightMenu = function () {
        $mdSidenav('right').toggle();
    };
    $scope.closeRightMenu = function () {
        $mdSidenav('right').toggle();
    };
}

function buildToggler(componentId) {
    return function () {
        $mdSidenav(componentId).toggle();
    }
}

app.controller('subjectteacherassoc', function ($scope, $http, $timeout, $compile, $window) {
    
    $scope.formtype ='Add';
    $scope.singledata={pluses:[{"class":"","amount":"","due_date":new Date()}]};

    $scope.singledata.pluses = [{"periods": "", "subject": "", "teacher": ""}];
    $scope.addplus = function () {
        $scope.singledata.pluses.push({"periods": "", "subject": "", "teacher": ""});
    };

    $scope.removeplus = function (index) {
        $scope.singledata.pluses.splice(index, 1);
    };
});


